mapper.insert:
INSERT INTO DATAGEAR_DASHBOARD_SHARE_SET
	(
	DBSS_ID, DBSS_ENABLE_PASSWORD, DBSS_PASSWORD
	)
VALUES
	(
	#{entity.id}, #{entity.enablePassword, jdbcType=VARCHAR}, #{entity.password}
	)

mapper.update:
UPDATE DATAGEAR_DASHBOARD_SHARE_SET SET
	DBSS_ENABLE_PASSWORD = #{entity.enablePassword, jdbcType=VARCHAR},
	DBSS_PASSWORD = #{entity.password}
WHERE
	DBSS_ID = #{entity.id}
